int ilosc String numerKolejnyDziecka String format1 = "yyyyMMdd"
use (groovy.time.TimeCategory) { dataUrodzenia = 1.days.ago.format(format1) }
ilosc = testRunner.testCase.testSteps['dane'].getPropertyValue('numerKolejnyDziecka').toInteger() + 1
log.info ilosc
if (ilosc < 10) numerKolejnyDziecka = "0" + ilosc.toString() else numerKolejnyDziecka = ilosc.toString()
def dane = testRunner.testCase.testSteps['dane'] dane.setPropertyValue("identyfikatorMatkiPesel", "81050913126-" + dataUrodzenia + "-" + numerKolejnyDziecka) dane.setPropertyValue("numerKolejnyDziecka", ilosc.toString())

import java.time.ZonedDateTime import java.time.format.DateTimeFormatter
def pracownikImieNazwisko = context.expand('${#Project#imieNazwiskoPracownika}') def pracownikIdentyfikator = context.expand('${#Project#identyfikatorUzytkownika}')
def formatter = DateTimeFormatter.ofPattern("yyyy-MM-dd'T'HH:mm:ss.SSS[XXX]") def teraz = ZonedDateTime.now() String data1 = teraz.minusHours(1).format(formatter) String data2 = teraz.format(formatter) String data3 = teraz.plusMonths(9).format(formatter)
def dane = testRunner.testCase.testSteps['dane'] dane.setPropertyValue("identyfikatorZdarzenia", UUID.randomUUID().toString()) dane.setPropertyValue("skierowanieIdentyfikator", UUID.randomUUID().toString()) dane.setPropertyValue("zdarzenieDataPoczatek", data1) dane.setPropertyValue("zdarzenieDataKoniec", data2) dane.setPropertyValue("podpisData", data2) dane.setPropertyValue("pracownikImieNazwisko", pracownikImieNazwisko) dane.setPropertyValue("pracownikIdentyfikator", pracownikIdentyfikator) dane.setPropertyValue("wykonanieObserwacjiData", data1) dane.setPropertyValue("przewidywanaDataPorodu", data3)

import pl.gov.csioz.p1.recepta.PodpisKwalifikowany;
import java.util.*;

String zmUrl = context.expand('${#Project#ObslugaZdarzenSUS.service.address}');
