public String generujToken(String sciezkaCertyfikatu, String aliasCertyfikatu, String hasloCertyfikatu, Map<String, Object> claims) {
try {
		InputStream input = new FileInputStream(sciezkaCertyfikatu);
		haslo = "1234567890"
		char[] haslo = hasloCertyfikatu.toCharArray();
		Map<String, Object> headers = new HashMap<>();
		headers.put("typ", "JWT")
		KeyStore keyStore = KeyStore.getInstance(KeyStore.getDefaultType());
		keyStore.load(input, haslo);
		Key privateKey = keyStore.getKey(aliasCertyfikatu, haslo);
		String token = Jwts.builder()
		.signWith(SignatureAlgorithm.RS256, privateKey)
		.setHeader(headers)
		.setClaims(claims)
		.compact();
		return token;
	} catch (Exception e) {
		log.error(e.getMessage());
		testRunner.fail("Token nie został wygenerowany");
	return null;
}
}

